document.addEventListener("DOMContentLoaded",function(){const a=document.querySelector(".navbar-toggler");a.addEventListener("click",function(){const b=a.querySelector(".navbar-toggler__icon .fa-solid");if(b.classList.contains("fa-bars")){b.classList.remove("fa-bars");b.classList.add("fa-xmark")}else{b.classList.remove("fa-xmark");b.classList.add("fa-bars")}})});